<template>    <div class="setbox">        <div class="title w-100 pl-20 font-16 text-3">商铺设置</div>        <div class="content pl-40 pt-30">            <el-row class="w-100" >                <el-col :span="24">                    <div class="float-left mr-15 mt-10 text-3">商铺logo</div>                    <div class="float-left">                        <el-upload                                class="avatar-uploader"                                action="https://jsonplaceholder.typicode.com/posts/"                                :show-file-list="false"                                :on-success="handleAvatarSuccess"                                :before-upload="beforeAvatarUpload">                            <img v-if="imageUrl" :src="imageUrl" class="avatar">                            <i v-else class="el-icon-plus avatar-uploader-icon"></i>                        </el-upload>                        <p class="text-9 font-12 mt-20">提示：请上传企业logo或商铺头像，建议大小750x750px。</p>                    </div>                </el-col>            </el-row>            <el-row class="mt-20">                <el-col :span="24">                    <div class="float-left mr-15 mt-10 text-3">商铺名称</div>                    <div class="shu float-left">                        <el-input                                placeholder="请输入内容"                                v-model="input"                                clearable>                        </el-input>                    </div>                </el-col>            </el-row>            <el-row class="mt-30">                <el-col :span="24">                    <div class="float-left mr-15 mt-10 text-3">主营产品</div>                    <div class="wid float-left">                        <el-input                                type="textarea"                                :rows="3"                                placeholder="请输入内容"                                v-model="textarea">                        </el-input>                    </div>                </el-col>            </el-row>            <el-row class="mt-30">                <el-col :span="24">                    <div class="float-left mr-15 mt-10 text-3">公司简介</div>                    <div class="wid float-left">                        <el-input                                type="textarea"                                :rows="3"                                placeholder="请输入内容"                                v-model="textarea">                        </el-input>                    </div>                </el-col>            </el-row>            <el-row>                <el-col :span="24">                    <el-button  type="danger" class="bg-red btn">发 布</el-button>                </el-col>            </el-row>        </div>    </div></template><script>    export default {        name: 'Set',        data() {            return {                imageUrl: ''            };        },        methods: {            handleAvatarSuccess(res, file) {                this.imageUrl = URL.createObjectURL(file.raw);            },            beforeAvatarUpload(file) {                const isJPG = file.type === 'image/jpeg';                const isLt2M = file.size / 1024 / 1024 < 2;                if (!isJPG) {                    this.$message.error('上传头像图片只能是 JPG 格式!');                }                if (!isLt2M) {                    this.$message.error('上传头像图片大小不能超过 2MB!');                }                return isJPG && isLt2M;            }        }    }</script><style scoped lang="scss">.setbox{    width: 1000px;    height: 970px;    margin: 30px 0 0 30px;    background: #ffffff;    .title{        height: 50px;        border: 1px solid #F5F5F5;        line-height: 50px;    }    .shu{        width: 500px;    }    .wid{        width: 850px;    }    .btn{        display: block;        width: 450px;        height: 50px;        margin: 80px auto;        background: red;        font-size: 18px;        font-weight:bold;    }}</style><style>    .avatar-uploader .el-upload {        border: 1px solid  #CCCCCC;        border-radius: 6px;        cursor: pointer;        position: relative;        overflow: hidden;    }    .avatar-uploader .el-upload:hover {        border-color: #409EFF;    }    .avatar-uploader-icon {        font-size: 28px;        color: #8c939d;        width: 122px;        height: 122px;        line-height: 122px;        text-align: center;    }    .avatar {        width: 122px;        height: 122px;        display: block;    }</style>